import pytest

from konstel import konstel


def test_hash_prot_with_stripped_chars():
	result = konstel.hash_prot_b32(' 	*')
	assert result == 'mfcqn6mh3bnp7vv6eirptvbqik5c65ip'

def test_sars2_nuc_fasta_to_spike_hash():
	result = konstel.sars2_spike_from_nuc_fasta('tests/data/test.fa')
	assert result['hash_full'] == 'mfcqn6mh3bnp7vv6eirptvbqik5c65ip'
	result = konstel.sars2_spike_from_nuc_fasta('tests/data/880396.fa')
	assert result['phoneme'] == 'hivakemi'

def phoneme_prot():
	phoneme = konstel.prot_to_phoneme('')
	assert phoneme == 'papoheme'

def test_generic():
	result = konstel.generic('ZEE')
	assert result['phoneme'] == 'hikasulo'


