import pytest

from konstel import konstel


def test_hash_prot_with_stripped_chars():
	h = konstel.hash_prot_b32(' 	*')
	assert h == 'mfcqn6mh3bnp7vv6eirptvbqik5c65ip'

def test_sars2_nuc_fasta_to_spike_hash():
	h = konstel.sars2_nuc_fasta_to_spike_hash('tests/data/test.fa')
	assert h == 'S:mfcqn6mh3bnp7vv6eirptvbqik5c65ip'

def test_prot_to_phoneme():
	phoneme = konstel.prot_to_phoneme('')
	assert phoneme == 'papoheme'